import { StyleSheet } from 'react-native';
import Colors from '../constants/Colors';

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  contentContainer: {
    padding: 20,
    borderColor: Colors.veryLightGray,
    borderBottomWidth: 1,
  },
  scroll: {
    backgroundColor: Colors.backgroundWhite,
  },

  // Titles
  title: {
    fontSize: 20,
    paddingVertical: 5,
    fontWeight: 'bold',
    color: Colors.darkGray,
  },
  subtitle: {
    color: Colors.darkGray,
    marginBottom: 10
  },

  // Icons
  iconButtonRow: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  icon: {
    color: "#fff",
  },

  // Buttons
  greenButton: Colors.greenButton,
  orangeButton: Colors.orangeButton,
  yellowButton: Colors.yellowButton,
  iconButton: {
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 12,
    paddingHorizontal: 15,
    borderRadius: 50,
    backgroundColor: Colors.green,
    shadowColor: Colors.darkGreen,
    shadowOffset: {
      width: 0,
      height: 5,
    },
    shadowOpacity: 1,
    shadowRadius: 0,
  },
  homeButton: {
    backgroundColor: Colors.green,
    shadowColor: Colors.darkGreen,
    shadowOffset: {
      width: 0,
      height: 5,
    },
    shadowOpacity: 1,
    shadowRadius: 0,
  },


  // Flat lists
  listRow: {
   borderColor: Colors.veryLightGray,
   borderBottomWidth: 1,
   flexDirection: 'row',
   marginLeft: 10,
   marginRight: 10,
   paddingTop: 10,
   paddingBottom: 10,
 },

   // CheckBox
   checkboxContainer: {
     flexDirection: "row",
     marginBottom: 20,
   },
   checkbox: {
     alignSelf: "center",
   },
   iconContainer: { //Can be used as a view conatiner or styling the image itself
     alignItems: 'center',
     borderColor: Colors.green,
     borderRadius: 10,
     borderWidth: 1,
     justifyContent: 'center',
     height: 50,
     width: 50
   },

 infoContainer: {
   flex: 5,
   paddingHorizontal: 10,
   flexDirection: "column",
   },
  infoTitle: {
    color: Colors.darkGray,
    fontSize: 18,
    fontWeight: 'bold'
  },
  infoItem: {
    color: Colors.gray,
    textAlign: 'left',
    fontSize: 14,
    fontWeight: 'bold'
   },

 timeContainer: {
   flex: 2,
   flexDirection: "row",
   justifyContent: 'flex-start',
   alignItems: "center",
 },
  timeItem: {
    color: Colors.gray,
    fontSize: 14,
    paddingTop: 4,
  },
 avatarContainer: {
   flex: 1,
   flexDirection: "row",
   justifyContent: "flex-end",
   alignItems: "center",
 },
 avatar: {
   borderRadius: 40,
   borderWidth: 1,
   height: 40,
   width: 40
 },
 caretContainer: {
   flex: 1,
   justifyContent: 'center',
   alignItems: "flex-end",
 },
 caret: {
   color: Colors.gray,
 },


});

export default styles;
